import React, { useState, useEffect, createContext } from 'react';
import { Layout, Typography , Menu, Button } from 'antd';

import DetailRainfall from './DetailInformation/DetailRainfall/DetailRainfall';

const { Header, Content } = Layout;
const { Title } = Typography;
const { SubMenu } = Menu;

import './App.css';

function App() {
  // 컴포넌트가 마운트될 때 데이터를 가져옴
  useEffect(() => {
  }, []);

  const renderContent = () => {
      switch (selectedMenu) {
          case '1-1':
            return <NotFoundPage />;
          case '1-2':
            return <NotFoundPage />
          case '1-4':
            return <NotFoundPage />
          case '2-1':
            return <NotFoundPage />
          case '3-1':
            return <NotFoundPage />
          case '4-1':
            return <NotFoundPage />
          case '4-2':
            return <NotFoundPage />
          case '6-2':
            return <NotFoundPage />
          default:
              return <NotFoundPage />;
              // return null;
      }
  };

  // 각 대메뉴의 대표 서브메뉴 설정
  const handleSubMenuClick = (menuKey) => {
    switch (menuKey) {
        case '1':
            setSelectedMenu('1-1');
            break;
        case '2':
            setSelectedMenu('2-1');
            break;
        case '3':
            setSelectedMenu('3-1');
            break;
        case '4':
            setSelectedMenu('4-1');
            break;
        case '5':
            setSelectedMenu('5-1');
            break;
        case '6':
            setSelectedMenu('6-2');
            break;
        default:
            break;
    }
  };

  const menuItems = [
    {
      label: '상세정보',
      key: '1',
      children: [
        { label: '상세강수량', key: '1-1' },
        { label: '실시간현황', key: '1-2' },
        { label: '연직레이더', key: '1-3' },
        { label: '기상센서', key: '1-4' },
      ],
      onTitleClick: () => handleSubMenuClick('1'),
    },
    {
      label: '통제현황',
      key: '2',
      children: [
        { label: '강수량 누적표', key: '2-1' },
        { label: '강수량 통계표', key: '2-2' },
        { label: '비교 통계', key: '2-3' },
      ],
      onTitleClick: () => handleSubMenuClick('2'),
    },
    {
      label: '결측현황',
      key: '3',
      children: [
        { label: '결측 현황', key: '3-1' },
      ],
      onTitleClick: () => handleSubMenuClick('3'),
    },
    {
      label: '장비상태',
      key: '4',
      children: [
        { label: 'NMS 모니터링', key: '4-1' },
        { label: 'UPS 및 전력감시', key: '4-2' },
      ],
      onTitleClick: () => handleSubMenuClick('4'),
    },
    {
      label: '점검기록',
      key: '5',
      children: [
        { label: '점검 목록', key: '5-1' },
      ],
      onTitleClick: () => handleSubMenuClick('5'),
    },
    {
      label: '시스템 설정',
      key: '6',
      children: [
        { label: '연직레이더 표출 설정', key: '6-1' },
        { label: '강우량 색상 범례', key: '6-2' },
        { label: '사용자 관리', key: '6-3' },
      ],
      onTitleClick: () => handleSubMenuClick('6'),
    },
  ];

  return (
    <Layout>
      <Header style={{ backgroundColor: 'black', height: '80px', padding: '20px 70px' }}>
        <Title style={{ color: 'white', margin: 0 ,display: 'inline'}}>검정관측소 관제시스템</Title>
        {/* <Button
          style={{ float:'right' , margin: '10px -30px 0px' }}
          type="primary"
          icon={<PoweroffOutlined />}
          // onClick={() => }
        >
          로그인
        </Button> */}
      </Header>
      <Menu 
          theme="dark" 
          mode="horizontal" 
          defaultSelectedKeys={['1-1']}
          selectedKeys={[selectedMenu]} 
          style={{ paddingLeft:'65px' , lineHeight: '60px' }}
          onSelect={({ key }) => setSelectedMenu(key)}
          items={menuItems}
        >
        </Menu>
          <ServerTime />
      <Content style={{ margin: '30px 65px' }}>
        {/* 여기에 나머지 컨텐츠를 추가하세요 */}
        {renderContent()}
      </Content>
    </Layout>
  );
}

export default App;
